<!-- Contact the Agent -->
<div class='contact__container contact__container-basic contact-agent-modal' style='margin-top:58px;'>
    <div class='contact__header'>
        <p>Like this Property?</p>
    </div>

    <div class='contact__wrapper'>
		<div class='contact__company'>
            <img class='contact__company-img' src='http://www.motihomes.com/Customers/5430/assets/images/3785DE71-155D-0078-0BA5D1687C016B53.jpg'>
            <div class='contact__company-info'>
                <p class='contact__company-name'>Coldwell Banker Residential Brokerage - <span class='contact__company-city'> Brookline </span></p>
                <p class='contact__company-note'>is happy to assist you</p>
            </div>
        </div>
        
        <div class='contact__form'>
            <p class='contact__form-required'><span>* </span>Required</p>
            <input class='contact__form-name' placeholder='Name' required/><span class='contact__required-marker'>*</span>
            <input class='contact__form-email' type='email' placeholder='Email' required/><span class='contact__required-marker'>*</span>
            <input class='contact__form-number' placeholder='Phone Number'/>
            <textarea class='contact__form-message' placeholder='Please send me more infomation about 101 Fernwood Rd, Brookline, MA. Thank you'></textarea>
            <button class='contact__form-submit'>Send</button>
        </div>
    </div>
</div>
<!-- Contact the Agent -->