<div class="newsletter-widget">
	<p class="newsletter-widget__header">Sign Up For Our Newsletter</p>

	<div class="newsletter-widget__signup-wrapper">
		<div class="newsletter-widget__input-wrapper">
			<form class="newsletter-widget__signup-form" name="newsletter-signup-form" action="#" method="post">
				<input type="text" name="email" class="newsletter-widget__signup-input" placeholder="Please enter your email"/>		
				<input type="submit" name="submit" class="newsletter-widget__signup-button" value="Submit"/>
			</form>
		</div>
	</div>

	<div class="newsletter-widget__connect-wrapper">
		<p class="newsletter-widget__connect-header">Connect With Us</p>
		<div class="newsletter-widget__connect--left-col">
			<!-- FACEBOOK CONNECT  BUTTON -->
			<div class="fb-like" data-href="https://facebook.com/boston" data-layout="button_count" data-action="like" data-show-faces="true" data-share="false"></div>
		</div>

		<div class="newsletter-widget__connect--right-col">
			<!-- PINTREST CONNECT  BUTTON -->
			<a href="https://www.pinterest.com/bostondotcom/dream-home-real-estate" target="_blank"><div class="newsletter-widget__pintrest-btn"><div class="newsletter-widget__pintrest-img"></div>Boston Real Estate</div></a>
		</div>
	</div>
	<script type="text/javascript" async defer src="//assets.pinterest.com/js/pinit.js"></script>
	
	<div id="fb-root"></div>
	<!-- FACEBOOK LIKE BUTTON JS -->
	<script>
		(function(d, s, id) {
			var js, fjs = d.getElementsByTagName(s)[0];
			if (d.getElementById(id)) return;
			js = d.createElement(s); js.id = id;
			js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.0";
			fjs.parentNode.insertBefore(js, fjs);
		}(document, 'script', 'facebook-jssdk'));
	</script>
</div>