<!-- MODAL: SHARE PROPERTY -->
<div class="share-modal">
    <div class="header">
        <a class="close-modal-btn" href="javascript:;"><img class="close-modal" src="images/listings/x-icon.JPG"></a>
        <p>Start Sharing</p>
    </div>
    <div class="content">
    	<div class='share-icons__wrapper'>
	    	<div class='share-icons icons--right-col'>
	        	<span class='st_facebook_large' displayText='Facebook'></span> 
	        	<span class='st_pinterest_large' displayText='Pinterest'></span>
		    </div>

	    	<div class='share-icons icons--left-col'>
				<span class='st_email_large' displayText='Email'></span> 
	        	<span class='st_twitter_large' displayText='Tweet'></span>
		    </div>
		</div>
    </div>
</div>
<!-- /MODAL: /SHARE PROPERTY -->